import axios from 'axios';

const apiUrl = "http://localhost:5216"

export default {

  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/items`)    
    return result.data;
  },

  // addTask: async(name)=>{
  //   console.log('addTask', name)
  //  const result= await axios.post(`${apiUrl}/items`,{name});
  //   return result.data;

  // },
  addTask: async(name) => {
    console.log('addTask', name);
    try {
        const result = await axios.post(`${apiUrl}/items`, { name });
        return result.data;
    } catch (error) {
        console.error("Error adding task:", error.message);
        throw error; // זרוק את השגיאה כדי שתוכל לטפל בה במקום אחר
    }
},


  setCompleted: async(id, isComplete)=>{
    console.log('setCompleted', {id, isComplete})
    //TODO
    return {};
  },

  deleteTask:async(itemID)=>{
    console.log('deleteTask',itemID)
    try{
      await axios.delete(`${apiUrl}/items/${itemID}`);
    }
    catch(error){
      console.error("Error deleting task:", error.message);
      throw error;
    }

  }
};
