  //   }
  //   catch(error){
  //     console.error("Error deletזing task:", error.message);
  //     throw error;
  //   }

  // }
  deleteTask: async (itemID) => {
    console.log('deleteTask', itemID);

    // בדוק אם ה-itemID תקין
    if (!itemID) {
        console.error("Invalid itemID provided");
        throw new Error("Invalid itemID");
    }

    try {
        const response = await axios.delete(`${apiUrl}/items/${itemID}`);

        // בדוק את סטטוס הקוד של התגובה
        if (response.status === 200) {
            console.log('Task deleted successfully');
            return response.data;
        } else {
            console.error('Failed to delete task:', response.statusText);
            throw new Error('Failed to delete task');
        }
    } catch (error) {
        console.error("Error deleting task:", error.message);
        throw error;
    }
}


};
